import React from 'react'
import styled from "styled-components";
const TodoItem = ({todo, actions}) => {

  return (
    <Wrapper>
      <span w10><input type="checkbox" value={todo.completed}  onChange={actions.toggleCompleteTodo}/></span>
      <span w60>{todo.text}</span>
      <span w15>{todo.completed}</span>
      <span w15><button type="button" onClick={() => actions.deleteTodo(todo.id)}>&times;</button></span>
    </Wrapper>
  )
}

const Wrapper = styled.div`
  display : flex ; 
  justify-content : space-between;
`

export default TodoItem
