import React from 'react'
import styled from "styled-components";
const TodoItem = ({todo, actions}) => {

  return (
    <Wrapper>
      <span className="w5"><input type="checkbox" value={todo.completed}  onChange={actions.toggleCompleteTodo}/></span>
      <span className="w60">{todo.text}</span>
      <span className="w20 center">{todo.completed === true ? "Completed" : "Active"}</span>
      <span className="w15 center"><button type="button" onClick={() => actions.deleteTodo(todo.id)}>&times;</button></span>
    </Wrapper>
  )
}

const Wrapper = styled.div`
  display : flex ; 
  padding : 0.75rem 0;
  justify-content : space-between;
  text-align :left; 
  .center{
    text-align : center;
  }
  & > span {
    display : block;       
    padding : 0.5rem 0;  
  } 
  .w5{
    width : 5% ; 
  }
  .w20{
    width : 20% ;
  }
  .w60{
    width : 60% ;
  }
  .w15{
    width : 15%;
  }
  @media screen and (max-width:768px){
    .w5{
      width : 10% ; 
    }
    .w60{
      width : 50% ;
      max-height : 4.8rem;
      overflow : hidden ; 
      white-space: nowrap;
      text-overflow : ellipsis ;
    }
    .w15, .w20{
      width : 20% ;
    }
  }
  button{
    padding: 0.5rem 1rem;
  }
`

export default TodoItem
