import React, { useState, useEffect } from "react";
import styled from "styled-components";
import {VisibilityFilter} from "../models/VisibilityFilter"


const MainSection = ({
  todos,
  todosCount,
  completedCount,
  visibilityFilter,
  actions,
}) => {
  const [selectFilter, setSelectFilter] = useState(visibilityFilter.id)
  const [todosList, setTodosList] = useState(todos);
  const handleChangeSelectFilter = e => {        
    setSelectFilter(e.target.value);    
  }
  useEffect(() => {
    switch(selectFilter){
      case "show_completed" : todos = todos.filter(todo => todo.completed);
      case "show_active": todos = todos.filter(todo => !todo.completed);
      default : return todos;
    }
  }, [selectFilter])
  return (
    <section>
      <Toolbar>
        <div className="check-completed-all">
          <input
            type="checkbox"
            checked={(todos.completed === completedCount)}
            onChange={actions.completeAllTodos}
            id="check-all"
          />
          <label htmlFor="check-all">Completed All</label>
        </div>
        <div className="center">
          <h4>Todos Count : {todosCount}</h4>
          <h4>Completed Count: {completedCount}</h4>
        </div>
        <div className="visibility-filter">
          <select value={selectFilter.id}  onChange={handleChangeSelectFilter}>
            {Object.values(VisibilityFilter).map(item => (
              <option key={item.id} value={item.id}>{item.displayName}</option>
            ))}
          </select>
        </div>
      </Toolbar>
      <div>
        {todos.map(todo => (
          <p key={todo.id}>{todo.text}</p>
        ))}
      </div>
    </section>
  );
};

const Toolbar = styled.div``;

export default MainSection;
