import React from 'react'
import styled from "styled-components";
const TodoItem = ({todo, actions}) => {

  return (
    <Wrapper>
      <span className="w10"><input type="checkbox" value={todo.completed}  onChange={actions.toggleCompleteTodo}/></span>
      <span className="w60">{todo.text}</span>
      <span className="w15">{todo.completed === true ? "Completed" : "Active"}</span>
      <span className="w15"><button type="button" onClick={() => actions.deleteTodo(todo.id)}>&times;</button></span>
    </Wrapper>
  )
}

const Wrapper = styled.div`
  display : flex ; 
  padding : 0.75rem 0;
  justify-content : space-between;
  text-align :left; 
  & > span {
    display : block;       
    border : 1px solid black;      
  } 
  .w10{
    width : 10% ; 
  }
  .w60{
    width : 60% ;
  }
  .w15{
    width : 15%;
  }
`

export default TodoItem
