import React from 'react'

const TodoItem = ({todo, actions}) => {

  return (
    <div>
      <span><input type="checkbox" value={todo.completed} checked={todo.completed} onChange={actions.toggleCompleteTodo}/></span>
      <span>{todo.text}</span>
      <span>{todo.completed}</span>
      <span><button>&times;</button></span>
    </div>
  )
}

export default TodoItem
