import React from 'react'
import styled from "styled-components"
import TodoTextInput from "./TodoTextInput"
const Header = styled.header`
  text-align: center;
  font-size: 1.5em;
`
export default ({addTodo}) => {
  return (
    <Header>
      <h1>Todos</h1>      
      <TodoTextInput placeholder="What needs to be done?" />
    </Header>
  )
}
