import React, { useState } from "react";
import styled from "styled-components";
const MainSection = ({
  todos,
  todosCount,
  completedCount,
  visibilityFilter,
  actions,
}) => {
  return (
    <section>
      <Toolbar>
        <div className="check-completed-all">
          <input
            type="checkbox"
            checked={(todos.completed == completedCount)}
            onChange={actions.completeAllTodos}
            id="check-all"
          />
          <label forHtml="check-all">Completed All</label>
        </div>
        <div className="center">
          <h4>Todos Count : {todosCount}</h4>
          <h4>Completed Count: {completedCount}</h4>
        </div>
      </Toolbar>
    </section>
  );
};

const Toolbar = styled.div``;

export default MainSection;
