import React from 'react'
import styled from "styled-components";
const TodoItem = ({todo, actions}) => {

  return (
    <Wrapper>
      <span w={10}><input type="checkbox" value={todo.completed}  onChange={actions.toggleCompleteTodo}/></span>
      <span w={60}>{todo.text}</span>
      <span w={15}>{todo.completed === true ? "Completed" : "Active"}</span>
      <span w={15}><button type="button" onClick={() => actions.deleteTodo(todo.id)}>&times;</button></span>
    </Wrapper>
  )
}

const Wrapper = styled.div`
  display : flex ; 
  padding : 0.75rem 0;
  justify-content : space-between;
  span {
    width : ${({w}) => `${w}%`};
    text-align: left'
  }
`

export default TodoItem
